mapper.select:
SELECT r.*
FROM sys_resources r
         LEFT JOIN sys_roles_and_resources rar ON r.id = rar.resource_id
         LEFT JOIN sys_users_and_roles uar ON uar.role_id = rar.role_id
WHERE uar.user_id = #{userId}
AND r.type = 'URL'
AND r.uri = #{uri}
AND r.method LIKE CONCAT('%', #{method}, '%');
